mapper.select:
select board_no , board_title , board_content , to_char(create_date, 'YYYY-MM-DD') as "create_date" , modify_date , count , del_ny , category_title as "category" , mem_nickname , table_no , mem_img , mem_no from board b join member using (mem_no) join category c on (b.category = c.category_no) where del_ny = 'N' and rep_ny = 'N'
and category = #{ category }
order by board_no desc

mapper.insert:
insert into
    board
    (
        board_no
      , board_title
      , board_content
      , category
      , mem_no

    )
    values
    (
    seq_board.nextval
  , #{ boardTitle }
  , #{ boardContent }
  , #{ category}
  , #{ memNo}

    )

insert into upload ( upload_no , origin_name , modify_name , group_board_no , table_no
) values ( seq_upload.nextval , #{ originName } , #{ changeName } , 1 , 10
)

mapper.update:
update upload
   set group_board_no = seq_board.currval
 where upload_no = #{uploadNo}

 update
     board
 set board_title = #{boardTitle}
   , board_content = #{boardContent}
   , modify_date = sysdate
where board_no = #{boardNo}

mapper.insert:
insert
 into   reply
 (
   reply_no
 , reply_content
   , reper_no
   , group_board_no
   , mem_no
   , table_no
 )
 values
 (
     seq_reply.nextval
  , #{replyContent}
    , #{reperNo}
    , #{groupBoardNo}
    , #{memNo}
    , 10
 )
